import React from 'react';
import './GameMenu.css';
import { useGame } from '../../contexts/GameContext';

const GameMenu = () => {
    const { state, dispatch } = useGame();
    const { player } = state;
    // Á°Æ‰øùinventory‰∏ç‰∏∫undefined
    const inventory = state.inventory || { items: [] };

    // ÊâìÂºÄËßíËâ≤‰ø°ÊÅØÁ™óÂè£
    const openCharacterInfo = () => {
        dispatch({
            type: 'OPEN_MODAL',
            payload: {
                type: 'character'
            }
        });
    };

    // ÊâìÂºÄË£ÖÂ§áÁ™óÂè£
    const openEquipment = () => {
        dispatch({
            type: 'OPEN_MODAL',
            payload: {
                type: 'equipment'
            }
        });
    };

    // ÊâìÂºÄÁâ©ÂìÅÊ†èÁ™óÂè£
    const openInventory = () => {
        dispatch({
            type: 'OPEN_MODAL',
            payload: {
                type: 'inventory'
            }
        });
    };

    // Â§ÑÁêÜÊäÄËÉΩËèúÂçïÁÇπÂáª (ÊöÇÊú™ÂÆûÁé∞)
    const handleSkillsClick = () => {
        dispatch({
            type: 'ADD_LOG',
            payload: {
                text: 'ÊäÄËÉΩÂäüËÉΩÂ∞öÊú™ÂÆûÁé∞',
                isCritical: false
            }
        });
    };

    // Â§ÑÁêÜËÆæÁΩÆËèúÂçïÁÇπÂáª (ÊöÇÊú™ÂÆûÁé∞)
    const handleSettingsClick = () => {
        dispatch({
            type: 'ADD_LOG',
            payload: {
                text: 'ËÆæÁΩÆÂäüËÉΩÂ∞öÊú™ÂÆûÁé∞',
                isCritical: false
            }
        });
    };

    // ËøîÂõû‰∏ªÈ°µÈù¢
    const handleReturnToSelect = () => {
        dispatch({ type: 'RETURN_TO_SELECT' });
    };

    return (
        <div className="game-menu">
            <div className="player-stats">
                <div className="stat-item">
                    <span className="stat-label">Á≠âÁ∫ß:</span>
                    <span className="stat-value">{player.level}</span>
                </div>
                <div className="stat-item">
                    <span className="stat-label">ÊîªÂáª:</span>
                    <span className="stat-value">{player.attack}</span>
                </div>
                <div className="stat-item">
                    <span className="stat-label">Èò≤Âæ°:</span>
                    <span className="stat-value">{player.defense}</span>
                </div>
                <div className="stat-item">
                    <span className="stat-label">Êö¥ÂáªÁéá:</span>
                    <span className="stat-value">{(player.critChance * 100).toFixed(1)}%</span>
                </div>
            </div>

            <div className="menu-buttons">
                <div className="menu-item" onClick={openCharacterInfo}>
                    <div className="menu-icon">üë§</div>
                    <div className="menu-text">ËßíËâ≤‰ø°ÊÅØ</div>
                    <div className="menu-badge">i</div>
                </div>

                <div className="menu-item" onClick={openEquipment}>
                    <div className="menu-icon">üõ°Ô∏è</div>
                    <div className="menu-text">Ë£ÖÂ§á</div>
                </div>

                <div className="menu-item" onClick={openInventory}>
                    <div className="menu-icon">üéí</div>
                    <div className="menu-text">Áâ©ÂìÅÊ†è</div>
                    <div className="menu-badge">{inventory.items.length}</div>
                </div>

                <div className="menu-item" onClick={handleSkillsClick}>
                    <div className="menu-icon">‚ú®</div>
                    <div className="menu-text">ÊäÄËÉΩ</div>
                </div>

                <div className="menu-item" onClick={handleSettingsClick}>
                    <div className="menu-icon">‚öôÔ∏è</div>
                    <div className="menu-text">ËÆæÁΩÆ</div>
                </div>

                <div className="menu-item" onClick={handleReturnToSelect}>
                    <div className="menu-icon">üè†</div>
                    <div className="menu-text">ËøîÂõû‰∏ªÈ°µ</div>
                </div>
            </div>
        </div>
    );
};

export default GameMenu;
