        critChance: 0.1 + monsterLevel * 0.005, // Âü∫Á°Ä10%ÔºåÊØèÁ∫ß+0.5%
        critMultiplier: 1.5,
        // ÊÄ™Áâ©ÁªèÈ™åÂÄºÔºöÂü∫Á°ÄÂÄº + Á≠âÁ∫ßÂä†Êàê
        expReward: 10 + monsterLevel * 5
    };
};

// ËÆ°ÁÆóÂçáÁ∫ßÊâÄÈúÄÁªèÈ™å
const calculateExpToNextLevel = (level) => {
    // ‰ΩøÁî®ÊåáÊï∞Â¢ûÈïøÂÖ¨Âºè: 100 * (level)^1.5
    return Math.floor(100 * Math.pow(level, 1.5));
};

// ÂàùÂßãÁä∂ÊÄÅ
const createInitialState = () => {
    // Â∞ùËØï‰ªélocalStorageËØªÂèñ‰øùÂ≠òÁöÑÁä∂ÊÄÅ
    const savedState = localStorage.getItem('rpgGameState');
    if (savedState) {
        try {
            return JSON.parse(savedState);
        } catch (e) {
            console.error('Failed to parse saved game state:', e);
        }
    }

    // ÈªòËÆ§ÂàùÂßãÁä∂ÊÄÅ
    return {
        // Ê∏∏ÊàèÂú∫ÊôØÁä∂ÊÄÅ: 'select' - ÊàòÂú∫ÈÄâÊã©È°µÈù¢, 'battle' - ÊàòÊñóÈ°µÈù¢
        gameScene: 'select',
        selectedBattlefield: null,

        player: {
            name: "ÂãáËÄÖ",
            level: 1,
            experience: 0,
            expToNextLevel: calculateExpToNextLevel(1),
            maxHealth: 100,
            currentHealth: 100,
            attack: 10,
            defense: 5,
            critChance: 0.3,
            critMultiplier: 2
        },

        monster: null,

        battleLogs: [],
        playerAttacking: false,
        monsterAttacking: false,
        playerDamaged: false,
        monsterDamaged: false,
        gameSpeed: 2000, // ÊØèÂõûÂêàÈó¥Èöî(ÊØ´Áßí)
        gameRunning: true,
        battlefields: battlefields
    };
};

// ReducerÂáΩÊï∞Êù•Â§ÑÁêÜÁä∂ÊÄÅÊõ¥Êñ∞
function gameReducer(state, action) {
    let newState;

    switch (action.type) {
        case 'SELECT_BATTLEFIELD':
            newState = {
                ...state,
                gameScene: 'battle',
                selectedBattlefield: action.payload,
                battleLogs: [{
                    id: Date.now(),
                    text: `ËøõÂÖ•${action.payload.name}ÊàòÂú∫ÔºÅ`,
                    isCritical: false
                }],
                // ÁîüÊàêÊñ∞ÊÄ™Áâ©
                monster: generateMonster(state.player.level, action.payload.id)
            };
            break;

        case 'RETURN_TO_SELECT':
            newState = {
                ...state,
                gameScene: 'select',
                monster: null,
                battleLogs: []
            };
            break;

        case 'PLAYER_ATTACK':
            newState = {
                ...state,
                playerAttacking: true
            };
            break;

        case 'PLAYER_ATTACK_END':
            newState = {
                ...state,
                playerAttacking: false
            };
            break;

        case 'MONSTER_DAMAGE':
            newState = {
                ...state,
                monsterDamaged: true,
                monster: {
                    ...state.monster,
                    currentHealth: Math.max(0, state.monster.currentHealth - action.payload.damage)
                },
                battleLogs: [
                    {
                        id: Date.now(),
                        text: action.payload.isCritical
                            ? `Êö¥ÂáªÔºÅ${state.player.name}ÂØπ${state.monster.name}ÈÄ†Êàê‰∫Ü${action.payload.damage}ÁÇπ‰º§ÂÆ≥ÔºÅ`
                            : `${state.player.name}ÂØπ${state.monster.name}ÈÄ†Êàê‰∫Ü${action.payload.damage}ÁÇπ‰º§ÂÆ≥„ÄÇ`,
                        isCritical: action.payload.isCritical
                    },
                    ...state.battleLogs.slice(0, 19)
                ]
            };
            break;

        case 'MONSTER_DAMAGE_END':
            newState = {
                ...state,
                monsterDamaged: false
            };
            break;

        case 'MONSTER_ATTACK':
            newState = {
                ...state,
                monsterAttacking: true
            };
            break;

        case 'MONSTER_ATTACK_END':
            newState = {
                ...state,
                monsterAttacking: false
            };
            break;

        case 'PLAYER_DAMAGE':
            newState = {
                ...state,
                playerDamaged: true,
                player: {
                    ...state.player,
                    currentHealth: Math.max(0, state.player.currentHealth - action.payload.damage)
                },
                battleLogs: [
                    {
                        id: Date.now(),
                        text: action.payload.isCritical
                            ? `Êö¥ÂáªÔºÅ${state.monster.name}ÂØπ${state.player.name}ÈÄ†Êàê‰∫Ü${action.payload.damage}ÁÇπ‰º§ÂÆ≥ÔºÅ`
                            : `${state.monster.name}ÂØπ${state.player.name}ÈÄ†Êàê‰∫Ü${action.payload.damage}ÁÇπ‰º§ÂÆ≥„ÄÇ`,
                        isCritical: action.payload.isCritical
                    },
                    ...state.battleLogs.slice(0, 19)
                ]
            };
            break;

        case 'PLAYER_DAMAGE_END':
            newState = {
                ...state,
                playerDamaged: false
            };
            break;

        case 'ADD_LOG':
            newState = {
                ...state,
                battleLogs: [
                    { id: Date.now(), text: action.payload.text, isCritical: action.payload.isCritical },
                    ...state.battleLogs.slice(0, 19)
                ]
            };
            break;

        case 'PLAYER_GAIN_EXPERIENCE':
            const expGained = action.payload.experience;
            let updatedPlayer = { ...state.player };
            updatedPlayer.experience += expGained;

            // Ê£ÄÊü•ÊòØÂê¶ÂçáÁ∫ß
            let leveledUp = false;
            while (updatedPlayer.experience >= updatedPlayer.expToNextLevel) {
                // ÂçáÁ∫ß
                updatedPlayer.level += 1;
                updatedPlayer.experience -= updatedPlayer.expToNextLevel;
                updatedPlayer.expToNextLevel = calculateExpToNextLevel(updatedPlayer.level);

                // Â±ûÊÄßÊèêÂçá
                const healthIncrease = Math.floor(20 + updatedPlayer.level * 5);
                updatedPlayer.maxHealth += healthIncrease;
                updatedPlayer.currentHealth += healthIncrease;
                updatedPlayer.attack += 2;
                updatedPlayer.defense += 1;

                // ÊØè10Á∫ßÂ¢ûÂä†Êö¥ÂáªÁéá
                if (updatedPlayer.level % 10 === 0) {
                    updatedPlayer.critChance += 0.05;
                }

                leveledUp = true;
            }

            const newLogs = leveledUp
                ? [
                    {
                        id: Date.now(),
                        text: `Ëé∑Âæó${expGained}ÁÇπÁªèÈ™åÔºÅÂçáÁ∫ßÂà∞${updatedPlayer.level}Á∫ßÔºÅ`,
                        isCritical: true
                    },
                    ...state.battleLogs
                ]
                : [
                    {
                        id: Date.now(),
                        text: `Ëé∑Âæó${expGained}ÁÇπÁªèÈ™åÔºÅ`,
                        isCritical: false
                    },
                    ...state.battleLogs
                ];

            newState = {
                ...state,
                player: updatedPlayer,
                battleLogs: newLogs.slice(0, 20)
            };
            break;

        case 'RESET_MONSTER':
            newState = {
                ...state,
                monster: generateMonster(state.player.level, state.selectedBattlefield.id),
                battleLogs: [
                    { id: Date.now(), text: "Êñ∞ÁöÑÊÄ™Áâ©Âá∫Áé∞‰∫ÜÔºÅ", isCritical: false },
                    ...state.battleLogs.slice(0, 19)
                ]
            };
            break;

        case 'RESET_PLAYER_HEALTH':
            newState = {
                ...state,
                player: {
                    ...state.player,
                    currentHealth: state.player.maxHealth
                },
                battleLogs: [
                    { id: Date.now(), text: `${state.player.name}ÊÅ¢Â§ç‰∫ÜÂÅ•Â∫∑ÔºÅ`, isCritical: false },
                    ...state.battleLogs.slice(0, 19)
                ]
            };
            break;

        case 'TOGGLE_GAME':
            newState = {
                ...state,
                gameRunning: !state.gameRunning
            };
            break;

        default:
            return state;
    }

    // ‰øùÂ≠òÁä∂ÊÄÅÂà∞localStorage
    localStorage.setItem('rpgGameState', JSON.stringify(newState));
    return newState;
}

// ÂàõÂª∫Context
const GameContext = createContext();

// Ëá™ÂÆö‰πâÈí©Â≠êÔºåÊñπ‰æøÁªÑ‰ª∂‰ΩøÁî®Context
export function useGame() {
    return useContext(GameContext);
}

// ProviderÁªÑ‰ª∂
export function GameProvider({ children }) {
    const [state, dispatch] = useReducer(gameReducer, null, createInitialState);

    // ÊàòÊñó‰º§ÂÆ≥ËÆ°ÁÆó
    const calculateDamage = (attacker, defender) => {
        let baseDamage = attacker.attack - defender.defense / 2;
        baseDamage = Math.max(1, baseDamage); // Á°Æ‰øùËá≥Â∞ë1ÁÇπ‰º§ÂÆ≥

        // ÈöèÊú∫Ê≥¢Âä® (¬±20%)
        const randomFactor = 0.8 + Math.random() * 0.4;
        return Math.floor(baseDamage * randomFactor);
    };

    // Ê∏∏Êàè‰∏ªÂæ™ÁéØ
    useEffect(() => {
        // Âè™Âú®ÊàòÊñóÂú∫ÊôØ‰∏îÊ∏∏ÊàèÊ≠£Âú®ËøêË°åÊó∂ÊâßË°åÊàòÊñóÂæ™ÁéØ
        if (state.gameScene !== 'battle' || !state.gameRunning || !state.monster) return;

        const gameLoop = setInterval(() => {
            // Ê£ÄÊü•Áé©ÂÆ∂ÊòØÂê¶Ê≠ª‰∫°
            if (state.player.currentHealth <= 0) {
                dispatch({
                    type: 'ADD_LOG',
                    payload: {
                        text: `ÊàòÊñóÂ§±Ë¥•ÔºÅ${state.player.name}Ë¢´${state.monster.name}ÂáªË¥•‰∫ÜÔºÅ`,
                        isCritical: true
                    }
                });

                // Âª∂ËøüÂêéËøîÂõûÈÄâÊã©È°µÈù¢
                setTimeout(() => {
                    dispatch({ type: 'RETURN_TO_SELECT' });
                }, 2000);

                return;
            }

            // Ê£ÄÊü•ÊÄ™Áâ©ÊòØÂê¶Ê≠ª‰∫°
            if (state.monster.currentHealth <= 0) {
                dispatch({
                    type: 'ADD_LOG',
                    payload: {
                        text: `ÊàòÊñóËÉúÂà©ÔºÅ${state.player.name}ÂáªË¥•‰∫Ü${state.monster.name}ÔºÅ`,
                        isCritical: true
                    }
                });

                // Ëé∑ÂæóÁªèÈ™åÂÄº
                dispatch({
                    type: 'PLAYER_GAIN_EXPERIENCE',
                    payload: { experience: state.monster.expReward }
                });

                // ÊÅ¢Â§çÁé©ÂÆ∂Ë°ÄÈáè
                setTimeout(() => {
                    dispatch({ type: 'RESET_PLAYER_HEALTH' });
                }, 1000);

                // ÁîüÊàêÊñ∞ÊÄ™Áâ©
                setTimeout(() => {
                    dispatch({ type: 'RESET_MONSTER' });
                }, 2000);

                return;
            }

            // Áé©ÂÆ∂ÊîªÂáª
            dispatch({ type: 'PLAYER_ATTACK' });

            setTimeout(() => {
                dispatch({ type: 'PLAYER_ATTACK_END' });

                const isCritical = Math.random() < state.player.critChance;
                let damage = calculateDamage(state.player, state.monster);

                if (isCritical) {
                    damage = Math.floor(damage * state.player.critMultiplier);
                }

                dispatch({
                    type: 'MONSTER_DAMAGE',
                    payload: { damage, isCritical }
                });

                setTimeout(() => {
                    dispatch({ type: 'MONSTER_DAMAGE_END' });

                    // ÊÄ™Áâ©ÊîªÂáª (Â¶ÇÊûúËøòÊ¥ªÁùÄ)
                    if (state.monster.currentHealth > 0) {
                        setTimeout(() => {
                            dispatch({ type: 'MONSTER_ATTACK' });

                            setTimeout(() => {
                                dispatch({ type: 'MONSTER_ATTACK_END' });

                                const monsterCritical = Math.random() < state.monster.critChance;
                                let monsterDamage = calculateDamage(state.monster, state.player);

                                if (monsterCritical) {
                                    monsterDamage = Math.floor(monsterDamage * state.monster.critMultiplier);
                                }

                                dispatch({
                                    type: 'PLAYER_DAMAGE',
                                    payload: { damage: monsterDamage, isCritical: monsterCritical }
                                });

                                setTimeout(() => {
                                    dispatch({ type: 'PLAYER_DAMAGE_END' });
                                }, 500);
                            }, 500);
                        }, 500);
                    }
                }, 500);
            }, 500);

        }, state.gameSpeed);

        return () => clearInterval(gameLoop);
    }, [state.gameScene, state.gameRunning, state.player, state.monster, state.selectedBattlefield]);

    const value = {
        state,
        dispatch,
        calculateExpToNextLevel
    };

    return (
        <GameContext.Provider value={value}>
            {children}
        </GameContext.Provider>
    );
}
